实现二 (ES5)
    (function(global, undefined){
        var taskList = [];
        // 类
        function LazyMan(){};
        LazyMan.prototype.eat = function(str){
            subscribe("eat", str);
            return this;
        };
        LazyMan.prototype.sleep = function(num){
            subscribe("sleep", num);
            return this;
        };
        LazyMan.prototype.sleepFirst = function(num){
            subscribe("sleepFirst", num);
            return this;
        };

        // 订阅
        function subscribe(){
            var param = {},
                args = Array.prototype.slice.call(arguments);
            if(args.length  0){
                run(taskList.shift());
            }
        }
        // 判断执行
        function run(option){
            var msg = option.msg,
                args = option.args;
            switch(msg){
                case "lazyMan": lazyMan.apply(null, args);break;
                case "eat": eat.apply(null, args);break;
                case "sleep": sleep.apply(null,args);break;
                case "sleepFirst": sleepFirst.apply(null,args);break;
                default:;
            }
        }
        // 具体方法
        function lazyMan(str){
            lazyManLog("Hi!This is "+ str +"!");
            publish();
        }
        function eat(str){
            lazyManLog("Eat "+ str +"~");
            publish();
        }
        function sleep(num){
            setTimeout(function(){
                lazyManLog("Wake up after "+ num);
                publish();
            }, num*1000);

        }
        function sleepFirst(num){
            setTimeout(function(){
                lazyManLog("Wake up after "+ num);
                publish();
            }, num*1000);
        }
        // 输出文字
        function lazyManLog(str){
            console.log(str);
        }
        // 暴露接口
        global.LazyMan = function(str){
            subscribe("lazyMan", str);
            setTimeout(function(){
                console.warn(JSON.stringify(taskList))
                publish();
            }, 0);
            return new LazyMan();
        };
    })(global);

